import React from 'react'
import SnackBar from './SnackBar'
import './App.css'
import {w3cwebsocket as WebSocket} from "websocket"

let webSocketFactory = {
	connectionTries: 3,
	connect: function(url) {
	  var ws = new WebSocket(url);
	  ws.addEventListener("error", e => {
		// readyState === 3 is CLOSED
		if (e.target.readyState === 3) {
		  this.connectionTries--;

		  if (this.connectionTries > 0) {
			setTimeout(() => this.connect(url), 5000);
		  } else {
			throw new Error("Maximum number of connection trials has been reached");
		  }

		}
	  });
	}
  };

let client = new WebSocket('ws://127.0.0.1:8000')
export default class App extends React.Component {
	constructor() {
		super()
		//this.getExpressServerStatus()
		this.state = {
			SnackBarOn: false,
			SnackBarText: null,
			WakeOnLanAvailable: false
		}
	}

	componentWillMount(){
		client.onopen = () => {
			console.log('WebSocket Client Connected')
			this.setState({WakeOnLanAvailable: true})
		}
		client.onmessage = (message) => {
			console.log(message)
		}
		client.onclose = () =>{
			this.setState({ WakeOnLanAvailable: false })
		}
	}

	render() {
		if (this.state.WakeOnLanAvailable === true) {
			return (
				<div>
					<button onClick={this.wol}>Wake On Lan</button>
					{this.state.SnackBarOn === true ? (
						<SnackBar text={this.state.SnackBarText} />
					) : (
						''
					)}
				</div>
			)
		} else {
			return null
		}
	}

	wol = () => {
		fetch('http://localhost:3002/WOL', {
			method: 'POST'
		}).then(() => {
			this.sendSnackBar('WOL request sent !')
		})
	}

	/*getExpressServerStatus = () => {
		fetch('http://localhost:3002/status', {
			method: 'GET'
		})
			.then((response) => {
				this.setState({ WakeOnLanAvailable: true })
			})
			.catch((err) => {
				console.log(err)
			})
		return true
	}*/

	sendSnackBar = (message) => {
		setTimeout(() => {
			this.setState({ SnackBarOn: false })
		}, 3000)
		this.setState({ SnackBarOn: true, SnackBarText: message })
	}
}
